import { useState } from "react";
import Axios from "axios";
import { useHistory, Link, useLocation } from "react-router-dom";

function BookReviewForm({props}) {
  const email = props;
  const [title,setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [review, setReview] = useState("");
  const [stars, setStars] = useState("");
  const [date, setDate] = useState("");
  const [book_cover, setBook_cover] = useState([]);

    const redirect = useHistory();

  const onSubmit= (event) => {
      event.preventDefault();

      fileUpload();

      Axios.post("http://localhost:3001/addreview", {
        email: email,
        title: title,
        author: author,
        review: review,
        stars: stars,
        date: date,
        book_cover: book_cover,
      }).then((response) => {
        if (response.data.correct) {
          redirect.push("profile", { email: response.data.email });
        }
      });
  }

  const fileUpload = () => {
    const reader = new FordmD

    reader.readAsDataURL(book_cover);

  }


  return (
    <div className="container">
      Book review form
      <form onSubmit={onSubmit}>
        <label>
          <h2>Title</h2>
          <input
            type="text"
            value={title}
            required
            onChange={(e) => setTitle(e.target.value)}
          />
        </label>
        <label>
          <h2>Author</h2>
          <input
            type="text"
            value={author}
            required
            onChange={(e) => setAuthor(e.target.value)}
          />
        </label>
        <label>
          <h2>Review</h2>
          <input
            type="text"
            value={review}
            required
            onChange={(e) => setReview(e.target.value)}
          />
        </label>
        <label>
          <h2>Stars</h2>
          <input
            type="number"
            min="1"
            max="5"
            value={stars}
            required
            onChange={(e) => setStars(e.target.value)}
          />
        </label>
        <label>
          <h2>Date</h2>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>
        <label for="img">Select image:</label>
        <input 
        type="file" 
        accept="image/*" 
        onChange={(e) => setBook_cover(e.target.files[0])}
        />
        <input type="submit" value="Add review" />
      </form>
    </div>
  );
}

export default BookReviewForm;
