import rev from "./Review.module.css";


function Review({props}) {
  const reviews = props.data;
  /* <article className={rev.review}>
     <h2>{review.title}</h2>
     <h3>{review.author}</h3>
     <p>hello</p>
     <p>Image... {review.stars}</p>
     <h4>{review.review}</h4>
     <h5>{review.date}</h5>
       </article>
     */

       const onClick = (event) => {
         event.preventDefault()
       }

  const map_reviews = reviews.map((review) => {
   const num = review.stars 
   const stars = num.toString() + ".png"

      if(review.filename == null){
          return (
            <article className={rev.review}>
              <p>{review.title}</p>
              <img
                className={rev.img}
                src={`http://localhost:3001/unknown.jpg`}
              />
              <img
                className={rev.img_stars}
                src={`http://localhost:3001/${stars}`}
              />
              <input type="image" src={`http://localhost:3001/unknown.jpg`} 
              onClick={onClick}/>
            </article>
          );
      } else {
          return (
            <article className={rev.review}>
              <img
                className={rev.img}
                src={`http://localhost:3001/${review.filename}`}
              />
              <img
                className={rev.img_stars}
                src={`http://localhost:3001/${stars}`}
              />
            </article>
          );
      }
  })

  return (
  <div className={rev.grid}>
      {map_reviews}
      </div>
  );
}

export default Review;
