import { useLocation, useHistory } from "react-router-dom";
import { useState, useEffect } from "react";
import Axios from "axios";
import Review from "../components/Review";

function Profile() {
  const location = useLocation();
  const email = location.state.email;

  const redirect = useHistory();

  const [reviews, setReviews] = useState([]);
  const [state, setState] = useState(false);

  const onClick = (event) => {
    event.preventDefault();

    redirect.push("/addreview", { email: email });
  };

  const signOut = (event) => {
    event.preventDefault();
    redirect.push("/signin");
  };

  useEffect(() => {
    const fetchData = () => {
      Axios.post("http://localhost:3001/reviews", {
        email: email,
      }).then((res) => {
        setReviews(res.data);
        setState(true);
      });
    };
    fetchData();
  }, []);

  const defaultValue = (
    <div>
      <h1>{email}'s book reviews!</h1>
      <button onClick={onClick}>Add book review</button>
      <button onClick={signOut}>Sign out</button>
    </div>
  );

  if (!state) {
    return (
      <div>
        <div>Loading...</div>
      </div>
    );
  } else if (reviews.data.length == 0) {
    return (
      <div>
        {defaultValue}
        <div>No reviews</div>
      </div>
    );
  } else {
    return (
      <div>
        {defaultValue}

        <Review props={reviews} />
      </div>
    );
  }
}

export default Profile;
