      <div>
        <h1>{email}'s book reviews!</h1>
        <button onClick={onClick}>Add book review</button>
        <button onClick={signOut}>Sign out</button>
      </div>
    ); 


if(!state){
  return (
    <div>
      <div>Loading...</div>
    </div>
  );

} else if (reviews.data.length == 0){
return (
  <div>
