import { useLocation, useHistory } from "react-router-dom";
import re from "./ReviewEdit.module.css";

function ReviewEdit() {
  const location = useLocation();
  const book = location.state.book;
  const num = book.stars;
  const stars = num.toString() + ".png";

  const onClick = (event) => {
      event.preventDefault();
  }
  return (
    <div className={re.margin}>
      <button onClick={onClick}>Save</button>

      <form onSubmit={onSubmit}>
        <label>
          <h2>Title</h2>
          <input
            type="text"
            value={title}
            required
            onChange={(e) => setTitle(e.target.value)}
          />
        </label>
        <label>
          <h2>Author</h2>
          <input
            type="text"
            value={author}
            required
            onChange={(e) => setAuthor(e.target.value)}
          />
        </label>
        <div className={brf.inputField}>
          <label>
            <h2>Review</h2>
            <input
              type="text"
              value={review}
              required
              onChange={(e) => setReview(e.target.value)}
            />
          </label>
        </div>

        <label className={brf.item}>
          <h2>Stars</h2>
          <input
            type="number"
            min="1"
            max="5"
            value={stars}
            required
            onChange={(e) => setStars(e.target.value)}
          />
        </label>
        <label>
          <h2>Date</h2>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>

        <input type="file" accept="image/*" onChange={fileUpload} />

        <input type="submit" value="Add review" />
      </form>
    </div>
  );
}

export default ReviewEdit;
