import { useLocation, useHistory } from "react-router-dom";
import { useState } from "react";
import re from "./ReviewEdit.module.css";

function ReviewEdit() {
  const location = useLocation();
  const book = location.state.book;
  const num = book.stars;
  const star = num.toString() + ".png";

    const [title, setTitle] = useState("");
    const [author, setAuthor] = useState("");
    const [review, setReview] = useState("");
    const [stars, setStars] = useState("");
    const [date, setDate] = useState("");

  const onSubmit = (event) => {
      event.preventDefault();

        Axios.post("http://localhost:3001/edit", {
          id: book.id_user,
          title: title,
          lastname: lastname,
          password: password,
        }).then((response) => {
          if (response.data.correct) {
            redirect.push("/profile", { email: response.data.email });
          }
        });

  }



  return (
    <div className={re.margin}>

      <form onSubmit={onSubmit}>
          <input type="submit" value="Save"/>
        <label>
          <input
            type="text"
            value={book.title}
            required
            onChange={(e) => setTitle(e.target.value)}
          />
        </label>
        <label>
          <input
            type="text"
            value={book.author}
            required
            onChange={(e) => setAuthor(e.target.value)}
          />
        </label>
        <div className={re.inputField}>
          <label>
            <input
              type="text"
              value={book.review}
              required
              onChange={(e) => setReview(e.target.value)}
            />
          </label>
        </div>

        <label className={re.item}>
          <input
            type="number"
            min="1"
            max="5"
            value={book.stars}
            required
            onChange={(e) => setStars(e.target.value)}
          />
        </label>
        <label>
          <input
            type="date"
            value={book.date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>
      </form>
    </div>
  );
}

export default ReviewEdit;
