import { useLocation, useHistory } from "react-router-dom";
import { useState } from "react";
import re from "./ReviewEdit.module.css";

function ReviewEdit() {
  const location = useLocation();
  const book = location.state.book;
  const num = book.stars;
  const star = num.toString() + ".png";

    const [title, setTitle] = useState("");
    const [author, setAuthor] = useState("");
    const [review, setReview] = useState("");
    const [stars, setStars] = useState("");
    const [date, setDate] = useState("");
    const [filename, setFilename] = useState("");
    const [book_cover, setBook_cover] = useState([]);

  const onSubmit = (event) => {
      event.preventDefault();
  }

    const fileUpload = (event) => {
      setFilename(event.target.files[0].name);
      setBook_cover(event.target.files[0]);
    };

  return (
    <div className={re.margin}>

      <form onSubmit={onSubmit}>
          <input type="submit" value="Save"/>
        <label>
          <input
            type="text"
            value={book.title}
            required
            onChange={(e) => setTitle(e.target.value)}
          />
        </label>
        <label>
          <input
            type="text"
            value={book.author}
            required
            onChange={(e) => setAuthor(e.target.value)}
          />
        </label>
        <div className={re.inputField}>
          <label>
            <input
              type="text"
              value={book.review}
              required
              onChange={(e) => setReview(e.target.value)}
            />
          </label>
        </div>

        <label className={re.item}>
          <h2>Stars</h2>
          <input
            type="number"
            min="1"
            max="5"
            value={book.stars}
            required
            onChange={(e) => setStars(e.target.value)}
          />
        </label>
        <label>
          <h2>Date</h2>
          <input
            type="date"
            value={book.date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>
      </form>
    </div>
  );
}

export default ReviewEdit;
